{!REQUIRESCRIPT('/soap/ajax/53.0/connection.js')} 
{!REQUIRESCRIPT('/soap/ajax/53.0/apex.js')} 

sforce.connection.sessionId = "{!$Api.Session_ID}";
var lang = sforce.connection.getUserInfo().userLanguage;
var confirmMsg = '';
var informMsg = '';

if ('ja' === lang) {
    confirmMsg = '案件と請求明細をコピーしますか？';
    informMsg = 'コピーが完了しました。確認してください。';
} else if ('vi' === lang) {
    confirmMsg = 'Bạn có muốn copy HBL SHIPMENT này không?';
    informMsg = 'Copy HBL SHIPMENT đã hoàn tất.';
} else {
    confirmMsg = 'Do you want to copy HBL SHIPMENT?';
    informMsg = 'Copying HBL SHIPMENT completed successfully.';
} 

if(window.confirm(confirmMsg)) {
    var clnoppoid = sforce.apex.execute('clofor_com_cfs.cntl_opportunityCopy', 'init',   {oppoid:'{!URLENCODE(CustomObject1__c.Id)}'}); 
    location.href= '/' + clnoppoid; 
    alert(informMsg);
}
