{!REQUIRESCRIPT('/soap/ajax/20.0/apex.js')} 

sforce.connection.sessionId = "{!$Api.Session_ID}";
var lang = sforce.connection.getUserInfo().userLanguage;
var confirmMsg = '';
var informMsg = '';

if ('ja' === lang) {
    confirmMsg = '為替レートを同期しますか？';
    informMsg = '為替レートの同期が正常に完了しました。';
} else if ('vi' === lang) {
    confirmMsg = 'Bạn có muốn đồng bộ hoá tỉ giá không?';
    informMsg = 'Tỉ giá đã được đồng bộ hoá thành công.';
} else {
    confirmMsg = 'Do you want to synchronize Exchange Rate?';
    informMsg = 'Synchronizing Exchange Rate completed successfully.';
} 

if(window.confirm(confirmMsg)) {
    sforce.apex.execute('clofor_com_cfs.ExchangeRateSynchronizationService', 'syncHBLShipmentAndSellingBuyings', {shipmentId:'{!URLENCODE(CustomObject1__c.Id)}'}); 
    location.href= '/' + '{!URLENCODE(CustomObject1__c.Id)}'; 
    alert(informMsg);
}
