{!REQUIRESCRIPT('/soap/ajax/20.0/connection.js')} 
{!REQUIRESCRIPT('/soap/ajax/20.0/apex.js')} 
var lang = sforce.connection.getUserInfo().userLanguage;
var confirmMsg = '';
var informMsg = '';

if ('ja' === lang) {
    confirmMsg = 'M B/L(混載)をコピーしますか？';
    informMsg = 'M B/L(混載)のコピーを完了しました。';
} else if ('vi' === lang) {
    confirmMsg = 'Bạn có muốn sao chép M B/L không?';
    informMsg = 'Sao chép M B/L hoàn thành.';
} else if ('ko' === lang){
    confirmMsg = 'M B/L(콘솔)을 복사하시겠습니까?';
    informMsg = 'M B/L(콘솔) 복사를 완료하였습니다.';
} else {
    confirmMsg = 'Are you sure that you want to copy M B/L - SHIPMENT?';
    informMsg = 'Copying M B/L - SHIPMENT completed'
} 

if(window.confirm(confirmMsg)) {
	var clonedId = sforce.apex.execute('clofor_com_cfs.cntl_opportunityCopy', 'copyMBLWithBuyingDetails', {mblId:'{!URLENCODE(MasterAnken__c.Id)}'}); 
	location.href= '/' + clonedId ; 

	alert(informMsg);
}
