WebLink.url:
{!REQUIRESCRIPT('/soap/ajax/20.0/connection.js')} 
{!REQUIRESCRIPT('/soap/ajax/20.0/apex.js')} 
sforce.connection.sessionId="{!$Api.Session_ID}";
var lang = sforce.connection.getUserInfo().userLanguage;
var confirmMsg = '';
var informMsg = '';

if ('ja' === lang) {
    confirmMsg = '為替レートを同期しますか？';
    informMsg = '為替レートの同期が正常に完了しました。';
} else if ('vi' === lang) {
    confirmMsg = 'Bạn có muốn đồng bộ hoá tỉ giá không?';
    informMsg = 'Đồng bộ hoá tỉ giá hoàn tất.';
} else if ('ko' === lang){
    confirmMsg = '환율 동기를 하시겠습니까?';
    informMsg = '환율 동기화가 완료되었습니다.';
} else {
    confirmMsg = 'Do you want to synchronize Exchange Rate?';
    informMsg = 'Syncronization Exchange Rate completed!';
}

if(window.confirm(confirmMsg)){
   sforce.apex.execute('clofor_com_cfs.ExchangeRateSynchronizationService', 'syncMBLBuyings', {masterShipmentId:'{!URLENCODE(MasterAnken__c.Id)}'}); 
   location.href= '/' + '{!URLENCODE(MasterAnken__c.Id)}'; 
   alert(informMsg);
}
